import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{o,c as s,a as c,b as n,d as e}from"./app-af427ef1.js";const l={},a=n("div",{style:{"text-align":"center"}},[n("p",null,"蜀道难"),n("p",null,"【作者】李白 【朝代】唐")],-1),r=n("p",null,"        噫吁嚱（yī xū xī），危乎高哉！蜀道之难，难于上青天！蚕(cán)丛及鱼凫(fú)，开国何茫然！尔来四万八千岁，不与秦塞通人烟。西当太白有鸟道，可以横绝峨眉巅。地崩山摧壮士死，然后天梯石栈相钩连。上有六龙回日之高标，下有冲波逆折之回川。黄鹤之飞尚不得过，猿猱(náo)欲度愁攀援。青泥何盘盘，百步九折萦(yíng)岩(yán)峦(luán)。扪(mén)参(shēn)历井仰胁息，以手抚膺(yīng)坐长叹。",-1),_=n("p",null,"        问君西游何时还？畏途巉(chán)岩不可攀。但见悲鸟号(háo)古木，雄飞雌从绕林间。又闻子规啼夜月，愁空山。蜀道之难，难于上青天，使人听此凋朱颜！连峰去天不盈尺，枯松倒挂倚绝壁。飞湍(tuān)瀑流争喧豗(huī)，砯(pīng)崖转石万壑雷。其险也如此，嗟尔远道之人胡为乎来哉！",-1),u=n("p",null,[e("        剑阁峥嵘(zhēng róng)而崔嵬(cuī wéi)，"),n("mark",null,"一夫当关，万夫莫开"),e("。所守或匪亲，化为狼与豺(chái)。朝避猛虎，夕避长蛇；磨牙吮(shǔn)血，杀人如麻。锦城虽云乐，不如早还家。蜀道之难，难于上青天，侧身西望长咨嗟(zī jiē)！")],-1);function h(i,d){return o(),s("div",null,[c(" more "),a,r,_,u])}const f=t(l,[["render",h],["__file","shu-dao-nan.html.vue"]]);export{f as default};
